import React from 'react'

const  Card = (props) => {

    return (
        <div className="card">
            {
                props.image &&
                <div className="card-image">

                <img src={props.image}/>
                
                </div>
            }
   
            <div className="card-info">
                { props.link && <a href={props.link} target="_blank">Follow</a> }
                
                <div className="c-info">
                    {props.username && <h2>{props.username}</h2>}
                    <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s.</p>
                    {props.following && <span>{props.following} Following - </span> }
                    {props.following && <span>{props.following} Followers</span>}
                    {props.repos && <span>{props.repos} repositories</span>}
                </div>
                
            </div>

            {props.error && <p>{props.error}</p>}

        </div>
    )
}

export default Card
