import React, { Component } from 'react'
import Search from "./components/Search"
import Card from "./components/Card"

import "./App.css"

class App extends Component {

  state = {
    username: '',
    image: '',
    link: '',
    following: '',
    followers: '',
    repos: '',
    error: ''
  }


  getProfile = async (e) => {
    e.preventDefault();

    const username = e.target.elements.username.value;
    const api = await fetch(`https://api.github.com/search/users?q=${username}`);
    const data = await api.json();

    console.log(data);

    //Check for the empty
    if (!username.trim()) {
      this.setState({
        error: 'Fill The Search Box Please !!'
      })
    } else {
      this.setState({
        username: data.items[0].login,
        image: data.items[0].avatar_url,
        link: data.items[0].html_url,
        following: data.items[0].following_url.length,
        followers: data.items[0].followers_url.length,
        repos:data.items[0].starred_url.length,
        error: ''
      })
    }


  }

  render() {
    return (
      <div>
        <Search getProfile={this.getProfile}/>
        <Card 
          username= {this.state.username} 
          image= {this.state.image} 
          link= {this.state.link} 
          following= {this.state.following} 
          followers= {this.state.followers}
          repos= {this.state.repos}
        />
      </div>
    )
  }
}

export default App
